import React, {useContext, useState} from "react";
import MyInput from "./components/input/MyInput";
import MyButton from "./components/button/MyButton";
import axios from "axios";
import {TokenContext} from "./Context";

function Login(){
    const TokenC = useContext(TokenContext);
    const {Token, setToken} = useState('');
    const {Error, setError} = useState('');
    const handleSubmit = (event) => {
        event.preventDefault();
        const formData = new FormData(event.target);
        axios.post('http://post.nel-it.ru:42113/login', formData)
            .then(res => setToken(res))
            .catch(err => setError(err))
        console.log(Token)
    };

    return (
            <form onSubmit={handleSubmit}>
                <TokenC.Provider value = {Token}>
                    <def> Login in your account</def>
                    <MyInput type="text" name="email" />
                    <MyInput type="text" name="password" />
                    <MyButton type="submit">Submit</MyButton>
                </TokenC.Provider>
            </form>
    );
}

export default Login;
