import React, {useContext, useState} from "react";
import axios from "axios";
import MyButton from "./components/button/MyButton";
import MyInput from "./components/input/MyInput";
function CreatAnnouncement(){
    const [announcement, setAnnouncement] = useState([]);
    [groupID, setgroupID] = useState(-1);
    const handleSubmit = (event) => {
        event.preventDefault();
        const formData = new FormData(event.target);
        axios.post('http://post.nel-it.ru:42113/creat', formData)
            .then(res => console.log(res))
            .catch(err => console.log(err))
    };
    function GroupSelect () {
        axios.get( 'http://post.nel-it.ru:42113/groups/get/forUser/' + localStorage.getItem('Token'))
            .then(res => setAnnouncement(res))
            .catch(err => console.log(err))
        return (
            <select>
                {announcement.map((announcement)=><option onClick={e => setgroupID(announcement.ID) }>{announcement}</option>)}

            </select>
        );
    }


    return (
        <form onSubmit={handleSubmit}>
            <def> Creat new account</def>
            <MyInput type="text" name="text" />
            <GroupSelect>

            </GroupSelect>
            <MyButton type="submit">Submit</MyButton>
        </form>
    );
}
export default CreatAnnouncement;
