import React, {createContext, useState} from "react";
import MyInput from "./components/input/MyInput";
import MyButton from "./components/button/MyButton";
import axios from "axios";
// import {TokenContext} from "./Context";

function Login(){
    const Token = createContext(null);
    const {NewToken, setNewToken} = useState('')
    const handleSubmit = (event) => {
        event.preventDefault();
        const formData = new FormData(event.target);
        axios.post('http://post.nel-it.ru:42113/login', formData)
            .then(res => setNewToken(res.data))
            // .catch(err => console.log(err))
        console.log(Token);
    };

    return (
        <Token.Provider value = {NewToken}>
            <form onSubmit={handleSubmit}>
                <def> Login in your account</def>
                <MyInput type="text" name="email" />
                <MyInput type="text" name="password" />
                <MyButton type="submit">Submit</MyButton>
            </form>
        </Token.Provider>
    );
}

export default Login;
