import React, {useContext, useState} from "react";
import MyInput from "./components/input/MyInput";
import MyButton from "./components/button/MyButton";
import axios from "axios";
import {TokenContext} from "./Context";

function Login(){
    const {NewToken, setNewToken} = useContext(TokenContext);
    const handleSubmit = (event) => {
        event.preventDefault();
        const formData = new FormData(event.target);
        axios.post('http://post.nel-it.ru:42113/login', formData)
            .then(res => setNewToken(res.data.access_token))
            // .catch(err => console.log(err))
        console.log(NewToken);
    };
    return (
        <TokenContext.Provider value = {NewToken}>
            <form onSubmit={handleSubmit}>
                <div> Login in your account</div>
                <MyInput type="text" name="email" />
                <MyInput type="text" name="password" />
                <MyButton type="submit">Submit</MyButton>
            </form>
        </TokenContext.Provider>
    );
}
export default Login;
